  const [gameover, setGameover] = useState(false);
  const [firstEmoji, setFirstEmoji] = useState();
  const [secondEmoji, setSecondEmoji] = useState();
  const [emojiMap, setEmojiMap] = useState(intialEmojiState);
  const [flippedMap, setFlippedMap] = useState(initialBoolState);
  const [matchedMap, setMatchedMap] = useState(initialBoolState);
  const [cardMap, setCardMap] = useState(intialState)

  const compareCardEmoji = () => {};

  const makeBoard = () => {
    const shuffleArray = (array) => {
      const clonedArray = [...array];
      for (let i = array.length - 1; i > 0; i--) {
        const j = Math.floor(Math.random() * (i + 1));
