      }
      return clonedArray;
    };

    const randomnArray = shuffleArray(emojiList);

    const tempStateArr = [...cardMap];
    let tracker = 0;
    for (let i = 0; i < 4; i++) {
      for (let j = 0; j < 4; j++) {
        tempStateArr[i][j].emoji = randomnArray[tracker];
        tempStateArr[i][j].flipped = false
        tempStateArr[i][j].matched = false
        tracker++;
      }
    };
    setCardMap(tempStateArr);
  };

  useEffect(() => {
    makeBoard();
  }, []);

