          return (
            <CardRow
              handleCardClick={handleCardClick}
              key={"card_row" + index}
              columnMap={columnMap}
              column={index}
            />
          );
        })}
      </div>
      <button onClick={newGameClick}>New Game</button>
    </>
  );
};

export default MemoryGame;
