import React, {useState, useEffect} from 'react';
import './Card.css';


const Card = props => {
  const [flipped, setFlipped] = useState(false);
  const [emojiMap, setEmojiMap] = useState();

  // useEffect(() => {
  //   let number = Math.floor(Math.random()*8)
  //   setEmojiMap(emojiList[number])
  // }, [])

  const cardClicked = event => {
    console.log(emojiMap)
    setFlipped(!flipped);
  }


return (
  <>
  {!flipped 
    ? (
      <div className="card" onClick={cardClicked}></div>
    ) : (
      <div className="cardFront" onClick={cardClicked}>{emojiMap}</div>

    )}
  </>
  )
};

export default Card;
