import React, { useState, useEffect } from "react";
import CardRow from "./CardRow";
import "./MemoryGame.css";

const MemoryGame = () => {
  const intialState = [
    [{}, {}, {}, {}],
    [{}, {}, {}, {}],
    [{}, {}, {}, {}],
    [{}, {}, {}, {}],
  ];

  const emojiList = [
    "ðŸ˜€",
    "ðŸ˜‚",
    "ðŸ¥°",
    "ðŸ˜˜",
    "ðŸ¤ª",
    "ðŸ¤“",
    "ðŸ¤©",
    "ðŸ¥³",
    "ðŸ˜€",
    "ðŸ˜‚",
    "ðŸ¥°",
    "ðŸ˜˜",
    "ðŸ¤ª",
    "ðŸ¤“",
    "ðŸ¤©",
    "ðŸ¥³",
  ];

  const [gameover, setGameover] = useState(false);
  const [firstEmoji, setFirstEmoji] = useState({ column: -1, row: -1, emoji: "" });
  const [secondEmoji, setSecondEmoji] = useState({ column: -1, row: -1, emoji: "" });
  const [cardMap, setCardMap] = useState(intialState);
  // 0 = no Cards currently clicked, 1 = 1 Card currently clicked, 2 = 2 Cards currently clicked (cannot go higher than 2)
  const [currentMove, setCurrentMove] = useState(0);

  const compareCardEmoji = (emojiOne, emojiTwo) => {
    if (emojiOne === emojiTwo) {
    }
  };

  const makeBoard = () => {
    const shuffleArray = (array) => {
      const clonedArray = [...array];
      for (let i = array.length - 1; i > 0; i--) {
        const j = Math.floor(Math.random() * (i + 1));
        [clonedArray[i], clonedArray[j]] = [clonedArray[j], clonedArray[i]];
      }
      return clonedArray;
    };

    const randomnArray = shuffleArray(emojiList);

    const tempStateArr = [...cardMap];
    let tracker = 0;
    for (let i = 0; i < 4; i++) {
      for (let j = 0; j < 4; j++) {
        tempStateArr[i][j].emoji = randomnArray[tracker];
        tempStateArr[i][j].flipped = false;
        tempStateArr[i][j].matched = false;
        tracker++;
      }
    }
    setCardMap(tempStateArr);
  };

  useEffect(() => {
    makeBoard();
  }, []);

  const newGameClick = (event) => {
    event.preventDefault();
    makeBoard();
  };

  const handleCardClick = (row, column) => {
    switch (currentMove) {
      case 0:
        {
          const tempCardMap = [...cardMap];
          tempCardMap[column][row].flipped = !tempCardMap[column][row].flipped;
          setFirstEmoji(tempCardMap[column][row].emoji);
          setCardMap({tempCardMap});
          setCurrentMove(1);
        }
        break;

      case 1:
        {
          const tempCardMap = [...cardMap];
          tempCardMap[column][row].flipped = !tempCardMap[column][row].flipped;
          setSecondEmoji(tempCardMap[column][row].emoji);
          setCardMap(tempCardMap);
          setCurrentMove(0);
        }
        break;

      case 2:
        break;
    }
  };

  return (
    <>
      <div class="memoryContainer">
        {cardMap.map((columnMap, index) => {
          return (
            <CardRow
              handleCardClick={handleCardClick}
              key={"card_row" + index}
              columnMap={columnMap}
              column={index}
            />
          );
        })}
      </div>
      <button onClick={newGameClick}>New Game</button>
    </>
  );
};

export default MemoryGame;
