import React, { useState, useEffect } from "react";
import { useImmer } from "use-immer";
import CardRow from "./CardRow";
import "./MemoryGame.css";

const MemoryGame = () => {
  const emojiList = [
    "ðŸ˜€",
    "ðŸ˜‚",
    "ðŸ¥°",
    "ðŸ˜˜",
    "ðŸ¤ª",
    "ðŸ¤“",
    "ðŸ¤©",
    "ðŸ¥³",
    "ðŸ˜€",
    "ðŸ˜‚",
    "ðŸ¥°",
    "ðŸ˜˜",
    "ðŸ¤ª",
    "ðŸ¤“",
    "ðŸ¤©",
    "ðŸ¥³",
  ];

  const [gameover, setGameover] = useState(false);
  const [firstEmoji, setFirstEmoji] = useImmer({ column: -1, row: -1, emoji: "" });
  const [secondEmoji, setSecondEmoji] = useImmer({ column: -1, row: -1, emoji: "" });
  const [cardMap, setCardMap] = useImmer([]);
  // 0 = no Cards currently clicked, 1 = 1 Card currently clicked, 2 = 2 Cards currently clicked (cannot go higher than 2)
  const [currentMove, setCurrentMove] = useState(0);

  // const compareCardEmoji = (emojiOne, emojiTwo, row, column) => {
  //   if (emojiOne === emojiTwo) {
  //     setCardMap(draft => {})
  //   }
  // };

  const makeBoard = () => {
    const shuffleArray = (array) => {
      for (let i = array.length - 1; i > 0; i--) {
        const j = Math.floor(Math.random() * (i + 1));
        [array[i], array[j]] = [array[j], array[i]];
      }
      return array;
    };

    const randomnArray = shuffleArray(emojiList);

    const tempArray = [
      [{}, {}, {}, {}],
      [{}, {}, {}, {}],
      [{}, {}, {}, {}],
      [{}, {}, {}, {}],
    ];

    let tracker = 0;
    for (let i = 0; i < 4; i++) {
      for (let j = 0; j < 4; j++) {
        tempArray[i][j].emoji = randomnArray[tracker];
        tempArray[i][j].flipped = false;
        tempArray[i][j].matched = false;
        tracker++;
      }
      setCardMap(tempArray);
    }
  };

  useEffect(() => {
    makeBoard();
  }, []);

  const newGameClick = (event) => {
    event.preventDefault();
    makeBoard();
  };

  const handleCardClick = (row, column) => {
    switch (currentMove) {
      case 0:
        {
          setCardMap((draft) => {
            draft[column][row].flipped = !draft[column][row].flipped;
          });
          setFirstEmoji((draft) => {
            draft.emoji = cardMap[column][row].emoji;
          });
          setCurrentMove(1);
        }
        break;

      case 1:
        {
          setCardMap((draft) => {
            draft[column][row].flipped = !draft[column][row].flipped;
          });
          setSecondEmoji((draft) => {
            draft.emoji = cardMap[column][row].emoji;
          });
          setCurrentMove(0);
        }
        break;
    }
  };

  return (
    <>
      <div class="memoryContainer">
        {cardMap.map((columnMap, index) => {
          return (
            <CardRow
              handleCardClick={handleCardClick}
              key={"card_row" + index}
              columnMap={columnMap}
              column={index}
            />
          );
        })}
      </div>
      <button onClick={newGameClick}>New Game</button>
    </>
  );
};

export default MemoryGame;
