import React from "react";
import Card from "./Card";
import "./CardRow.css"

const CardRow = (props) => {
  const { rowEmojis, rowFlipped, rowMatched, column } = props;
  return (
    <div class="cardRowContainer">
      {rowEmojis.map((emoji, index) => {
        return <Card column={column} row={index} key={"card" + index} emoji={emoji} flipped={rowFlipped[index]} matched={rowMatched[index]}   />;
      })}
    </div>
  );
};

export default CardRow;
